public static DocumentBuilderFactory newInstance(){
  DocumentBuilderFactory factory=DocumentBuilderFactory.newInstance();
  String errorMessage = " is probably not supported by " + factory.getClass().getCanonicalName();
  setAttribute(factory, XMLConstants.ACCESS_EXTERNAL_DTD, "");
  setAttribute(factory, XMLConstants.ACCESS_EXTERNAL_SCHEMA, "");
  setFeature(factory, "http://apache.org/xml/features/disallow-doctype-decl", true);
  setFeature(factory, "http://xml.org/sax/features/external-general-entities", false);
  setFeature(factory, "http://xml.org/sax/features/external-parameter-entities", false);
  setFeature(factory, "http://apache.org/xml/features/nonvalidating/load-external-dtd", false);
  try {
    factory.setXIncludeAware(false);
    factory.setExpandEntityReferences(false);
  } catch (Exception e) {
    if (log.isWarnEnabled()) {
      log.warn("Caught " + e.getMessage() + " attempting to configure your XML parser.");
    }
  }
  return factory;
}

private void setAttribute(DocumentBuilderFactory factory, String attribute, String value) {
  try {
    factory.setAttribute(attribute, value);
  } catch (IllegalArgumentException e) {
    if (log.isWarnEnabled()) {
      log.warn(attribute + errorMessage);
    }
  }
}

private void setFeature(DocumentBuilderFactory factory, String feature, boolean value) {
  try {
    factory.setFeature(feature, value);
  } catch (ParserConfigurationException e) {
    if (log.isWarnEnabled()) {
      log.warn("FEATURE '" + feature + "'" + errorMessage);
    }
  }
}

