public static DocumentBuilderFactory newInstance(){
  DocumentBuilderFactory factory=DocumentBuilderFactory.newInstance();
  setAttributeOrFeature(factory, XMLConstants.ACCESS_EXTERNAL_DTD, "");
  setAttributeOrFeature(factory, XMLConstants.ACCESS_EXTERNAL_SCHEMA, "");
  setAttributeOrFeature(factory, "http://apache.org/xml/features/disallow-doctype-decl", true);
  setAttributeOrFeature(factory, "http://xml.org/sax/features/external-general-entities", false);
  setAttributeOrFeature(factory, "http://xml.org/sax/features/external-parameter-entities", false);
  setAttributeOrFeature(factory, "http://apache.org/xml/features/nonvalidating/load-external-dtd", false);
  try {
    factory.setXIncludeAware(false);
    factory.setExpandEntityReferences(false);
  }
catch (  Exception e) {
    if (log.isWarnEnabled()) {
      log.warn("Caught " + e.getMessage() + " attempting to configure your XML parser.");
    }
  }
  return factory;
}

private static void setAttributeOrFeature(DocumentBuilderFactory factory, String attributeOrFeature, Object value) {
  try {
    if (value instanceof Boolean) {
      factory.setFeature(attributeOrFeature, (Boolean) value);
    } else {
      factory.setAttribute(attributeOrFeature, value);
    }
  } catch (Exception e) {
    if (log.isWarnEnabled()) {
      log.warn(attributeOrFeature + " not supported by " + factory.getClass().getCanonicalName());
    }
  }
}

