Method_Improvements.Final code:
public static DocumentBuilderFactory newInstance(){
  try (DocumentBuilderFactory factory = DocumentBuilderFactory.newInstance()){
    setFactoryAttribute(factory, XMLConstants.ACCESS_EXTERNAL_DTD, "");
    setFactoryAttribute(factory, XMLConstants.ACCESS_EXTERNAL_SCHEMA, "");
    setFactoryFeature(factory, "http://apache.org/xml/features/disallow-doctype-decl", true);
    setFactoryFeature(factory, "http://xml.org/sax/features/external-general-entities", false);
    setFactoryFeature(factory, "http://xml.org/sax/features/external-parameter-entities", false);
    setFactoryFeature(factory, "http://apache.org/xml/features/nonvalidating/load-external-dtd", false);
    factory.setXIncludeAware(false);
    factory.setExpandEntityReferences(false);
    return factory;
  }
}

private static void setFactoryAttribute(DocumentBuilderFactory factory, String attribute, String value) {
  try {
    factory.setAttribute(attribute, value);
  } catch (IllegalArgumentException e) {
    if (log.isWarnEnabled()) {
      log.warn(attribute + " property not supported by " + factory.getClass().getCanonicalName());
    }
  }
}

private static void setFactoryFeature(DocumentBuilderFactory factory, String feature, boolean value) {
  try {
    factory.setFeature(feature, value);
  } catch (ParserConfigurationException e) {
    if (log.isWarnEnabled()) {
      log.warn("FEATURE '" + feature + "' is probably not supported by " + factory.getClass().getCanonicalName());
    }
  }
}

