private void registerEndpointAdapters(Element element,Object source,ParserContext parserContext){
  // Create endpoint adapter definition
  RootBeanDefinition adapterDef=createBeanDefinition(DefaultMethodEndpointAdapter.class,source);
  ManagedList<BeanMetadataElement> argumentResolvers=new ManagedList<BeanMetadataElement>();
  argumentResolvers.setSource(source);
  ManagedList<BeanMetadataElement> returnValueHandlers=new ManagedList<BeanMetadataElement>();
  returnValueHandlers.setSource(source);

  // Add argument resolvers and return value handlers
  addProcessors(argumentResolvers, returnValueHandlers, MessageContextMethodArgumentResolver.class, source);
  ... 
  addProcessors(argumentResolvers, returnValueHandlers, StaxPayloadMethodArgumentResolver.class, source);

  // Handle marshaller attribute
  if (element.hasAttribute("marshaller")) {
    RuntimeBeanReference marshallerReference=new RuntimeBeanReference(element.getAttribute("marshaller"));

    // Use marshaller as unmarshaller if no unmarshaller attribute
    RuntimeBeanReference unmarshallerReference = element.hasAttribute("unmarshaller") ? new RuntimeBeanReference(element.getAttribute("unmarshaller")) : marshallerReference;

    RootBeanDefinition marshallingProcessorDef=createBeanDefinition(MarshallingPayloadMethodProcessor.class,source);
    marshallingProcessorDef.getPropertyValues().add("marshaller",marshallerReference);
    marshallingProcessorDef.getPropertyValues().add("unmarshaller",unmarshallerReference);
    addProcessors(argumentResolvers, returnValueHandlers, marshallingProcessorDef);
  }

  // Set method argument resolvers and return value handlers
  adapterDef.getPropertyValues().add("methodArgumentResolvers",argumentResolvers);
  adapterDef.getPropertyValues().add("methodReturnValueHandlers",returnValueHandlers);

  // Register endpoint adapter definition
  parserContext.getReaderContext().registerWithGeneratedName(adapterDef);
}

private void addProcessors(ManagedList<BeanMetadataElement> argumentResolvers, ManagedList<BeanMetadataElement> returnValueHandlers, Object resolver, Object source) {
  argumentResolvers.add(createBeanDefinition(resolver, source));
  returnValueHandlers.add(createBeanDefinition(resolver, source));
}

