@Test void testMaxConnections() throws Exception {
  private static final String URL1 = "https://www.example.com";
  private static final String URL2 = "http://www.example.com:8080";
  private static final String URL3 = "http://www.springframework.org";

  HttpRoute route1 = createHttpRoute(URL1, true);
  assertRouteProperties(route1, "www.example.com", 443, true);

  HttpRoute route2 = createHttpRoute(URL2, false);
  assertRouteProperties(route2, "www.example.com", 8080, false);

  HttpRoute route3 = createHttpRoute(URL3, false);
  assertRouteProperties(route3, "www.springframework.org", 80, false);

  HttpComponents5ClientFactory clientFactory=new HttpComponents5ClientFactory();
  Map<String,String> maxConnectionsPerHost=new HashMap<>();
  maxConnectionsPerHost.put(URL1,"1");
  maxConnectionsPerHost.put(URL2,"7");
  maxConnectionsPerHost.put(URL3,"10");
  clientFactory.setMaxTotalConnections(2);
  clientFactory.setMaxConnectionsPerHost(maxConnectionsPerHost);
  CloseableHttpClient client=clientFactory.getObject();
  assertThat(client).isNotNull();
  PoolingHttpClientConnectionManager poolingHttpClientConnectionManager=clientFactory.getConnectionManager();
  assertThat(poolingHttpClientConnectionManager.getMaxPerRoute(route1)).isEqualTo(1);
  assertThat(poolingHttpClientConnectionManager.getMaxPerRoute(route2)).isEqualTo(7);
  assertThat(poolingHttpClientConnectionManager.getMaxPerRoute(route3)).isEqualTo(10);
}

private HttpRoute createHttpRoute(String url, boolean isSecure) throws URISyntaxException {
  URI uri=new URI(url);
  HttpHost host=new HttpHost(uri.getScheme(),uri.getHost(),getPort(uri));
  return new HttpRoute(host,null,isSecure);
}

private void assertRouteProperties(HttpRoute route, String expectedHost, int expectedPort, boolean expectedIsSecure) {
  assertThat(route.isSecure()).isEqualTo(expectedIsSecure);
  assertThat(route.getTargetHost().getHostName()).isEqualTo(expectedHost);
  assertThat(route.getTargetHost().getPort()).isEqualTo(expectedPort);
}

