@Override public void onLeaderboardScoresDownloaded(SteamLeaderboardHandle leaderboard,SteamLeaderboardEntriesHandle entries,int numEntries){
  System.out.println(String.format("Leaderboard scores downloaded: handle=%s, entries=%s, count=%d", leaderboard, entries, numEntries));
  int[] details=new int[16];
  for (int i=0; i < numEntries; i++) {
    SteamLeaderboardEntry entry=new SteamLeaderboardEntry();
    if (userStats.getDownloadedLeaderboardEntry(entries,i,entry,details)) {
      int numDetails=entry.getNumDetails();
      StringBuilder sb = new StringBuilder();
      sb.append(String.format("Leaderboard entry #%d: accountID=%d, globalRank=%d, score=%d, numDetails=%d", i, entry.getSteamIDUser().getAccountID(), entry.getGlobalRank(), entry.getScore(), numDetails));
      for (int detail=0; detail < numDetails; detail++) {
        sb.append(String.format("  ... detail #%d=%d", detail, details[detail]));
      }
      if (friends.requestUserInformation(entry.getSteamIDUser(),false)) {
        sb.append("  ... requested user information for entry");
      }
 else {
        sb.append(String.format("  ... user name is '%s'", friends.getFriendPersonaName(entry.getSteamIDUser())));
        int smallAvatar=friends.getSmallFriendAvatar(entry.getSteamIDUser());
        if (smallAvatar != 0) {
          int w=utils.getImageWidth(smallAvatar);
          int h=utils.getImageHeight(smallAvatar);
          sb.append(String.format("  ... small avatar size: %dx%d pixels", w, h));
          ByteBuffer image=ByteBuffer.allocateDirect(w * h * 4);
          try {
            if (utils.getImageRGBA(smallAvatar,image)) {
              sb.append("  ... small avatar retrieve avatar image successful");
              int nonZeroes=w * h;
              for (int y=0; y < h; y++) {
                for (int x=0; x < w; x++) {
                  if (image.getInt(y * w + x) == 0) {
                    nonZeroes--;
                  }
                }
              }
              if (nonZeroes == 0) {
                System.err.println("Something's wrong! Avatar image is empty!");
              }
            }
 else {
              sb.append("  ... small avatar retrieve avatar image failed!");
            }
          }
 catch (SteamException e) {
            e.printStackTrace();
          }
        }
 else {
          sb.append("  ... small avatar image not available!");
        }
      }
      System.out.println(sb.toString());
    }
  }
}

