@Override public void onLeaderboardScoresDownloaded(SteamLeaderboardHandle leaderboard,SteamLeaderboardEntriesHandle entries,int numEntries){
  logger.info("Leaderboard scores downloaded: handle=" + leaderboard.toString() + ", entries="+ entries.toString()+ ", count="+ numEntries);
  int[] details=new int[16];
  for (int i=0; i < numEntries; i++) {
    SteamLeaderboardEntry entry=new SteamLeaderboardEntry();
    if (userStats.getDownloadedLeaderboardEntry(entries,i,entry,details)) {
      int numDetails=entry.getNumDetails();
      logger.info("Leaderboard entry #" + i + ": accountID="+ entry.getSteamIDUser().getAccountID()+ ", globalRank="+ entry.getGlobalRank()+ ", score="+ entry.getScore()+ ", numDetails="+ numDetails);
      for (int detail=0; detail < numDetails; detail++) {
        logger.info("  ... detail #" + detail + "="+ details[detail]);
      }
      processAvatar(entry.getSteamIDUser());
    }
  }
}

private void processAvatar(SteamID userSteamId) {
  if (friends.requestUserInformation(userSteamId,false)) {
    logger.info("  ... requested user information for entry");
  }
 else {
    logger.info("  ... user name is '" + friends.getFriendPersonaName(userSteamId) + "'");
    int smallAvatar=friends.getSmallFriendAvatar(userSteamId);
    if (smallAvatar != 0) {
      int w=utils.getImageWidth(smallAvatar);
      int h=utils.getImageHeight(smallAvatar);
      logger.info("  ... small avatar size: " + w + "x"+ h+ " pixels");
      ByteBuffer image=ByteBuffer.allocateDirect(w * h * 4);
      try {
        if (utils.getImageRGBA(smallAvatar,image)) {
          logger.info("  ... small avatar retrieve avatar image successful");
          int nonZeroes=w * h;
          for (int y=0; y < h; y++) {
            for (int x=0; x < w; x++) {
              if (image.getInt(y * w + x) == 0) {
                nonZeroes--;
              }
            }
          }
          if (nonZeroes == 0) {
            logger.error("Something's wrong! Avatar image is empty!");
          }
        }
 else {
          logger.info("  ... small avatar retrieve avatar image failed!");
        }
      }
 catch (SteamException e) {
        e.printStackTrace();
      }
    }
 else {
      logger.info("  ... small avatar image not available!");
    }
  }
}

