@Override protected void processInput(String input) throws SteamException {
  String[] parts = input.split(' ', 2);
  String command = parts[0];
  String parameters = parts.length > 1 ? parts[1] : '';
  switch (command) {
    case 'lobby list':
      listLobbies();
      break;
    case 'lobby request':
      requestLobby(parameters);
      break;
    case 'lobby create':
      createLobby(parameters);
      break;
    case 'lobby join':
      joinLobby(parameters);
      break;
    case 'lobby leave':
      leaveLobby(parameters);
      break;
    case 'lobby invite':
      inviteToLobby(parameters);
      break;
    case 'lobby data':
      showLobbyData(parameters);
      break;
    case 'lobby chat':
      chatInLobby(parameters);
      break;
    case 'get lobby member data':
      getLobbyMemberData(parameters);
      break;
    case 'set lobby member data':
      setLobbyMemberData(parameters);
      break;
    default:
      friends.processInput(input);
  }
}

private SteamID getValidLobbyID(String parameters, String command) {
  long id = Long.parseLong(parameters.substring(command.length()),16);
  if (lobbies.containsKey(id)) {
    return lobbies.get(id);
  } else {
    System.err.println('No lobby found: ' + Long.toHexString(id));
    return null;
  }
}

