private static final String P2P_SEND = "p2p send ";
private static final String P2P_CLOSE = "p2p close ";
private static final String P2P_LIST = "p2p list";
private static final String AUTH_TICKET = "auth ticket ";

private SteamID getSteamID(int id) {
    SteamID steamID = null;
    if (remoteUserIDs.containsKey(id)) {
        steamID = remoteUserIDs.get(id);
    } else if (friends.isFriendAccountID(id)) {
        steamID = friends.getFriendSteamID(id);
    } else {
        System.out.println("Error: unknown userID " + id + " (no friend, not connected)");
    }
    return steamID;
}

