/** 
 * Transforms a client query into a potentially nested/complex query by expanding each metric into its respective query plan - and then merging the plans together into a consolidated query.
 * @param query The client query.
 * @return A query that reflects each metric's individual query plan.
 */
private Query expandMetricQueryPlans(Query query){
  List<QueryPlan> toMerge=query.getMetricProjections().stream().map(projection -> projection.resolve(query)).collect(Collectors.toList());
  List<QueryPlan> mergedPlans=merger.merge(toMerge);
  if (mergedPlans.size() != 1) {
    throw new UnsupportedOperationException("Incompatible metrics in client query.  Cannot merge " + "into a single query");
  }
  QueryPlan mergedPlan=mergedPlans.get(0);
  QueryPlanTranslator queryPlanTranslator=new QueryPlanTranslator(query,metaDataStore,merger);
  Query merged=(mergedPlan == null) ? QueryPlanTranslator.addHiddenProjections(metaDataStore,query).build() : queryPlanTranslator.translate(mergedPlan);
  for (  Optimizer optimizer : optimizers) {
    SQLTable table=(SQLTable)query.getSource();
    if (table.getHints().contains(optimizer.negateHint())) {
      continue;
    }
    if (!table.getHints().contains(optimizer.hint())) {
      continue;
    }
    if (optimizer.canOptimize(merged)) {
      merged=optimizer.optimize(merged);
    }
  }
  return merged;
}

public static ConfigFile.ConfigFileType toType(String path){
  String lowerCasePath=path.toLowerCase(Locale.ROOT);
  if (lowerCasePath.endsWith("db/variables.hjson")) {
    return ConfigFile.ConfigFileType.VARIABLE;
  }
 else   if (lowerCasePath.endsWith("models/variables.hjson")) {
    return ConfigFile.ConfigFileType.VARIABLE;
  }
 else   if (lowerCasePath.equals("models/security.hjson")) {
    return ConfigFile.ConfigFileType.SECURITY;
  }
 else   if (DB_FILE.matcher(lowerCasePath).matches()) {
    return ConfigFile.ConfigFileType.DATABASE;
  }
 else   if (TABLE_FILE.matcher(lowerCasePath).matches()) {
    return ConfigFile.ConfigFileType.TABLE;
  }
 else   if (NAME_SPACE_FILE.matcher(lowerCasePath).matches()) {
    return ConfigFile.ConfigFileType.NAMESPACE;
  }
 else {
    return ConfigFile.ConfigFileType.UNKNOWN;
  }
}

private static Map<Class<? extends Annotation>,Annotation> buildAnnotations(Dimension dimension){
  Map<Class<? extends Annotation>,Annotation> annotations=new HashMap<>();
  annotations.put(ColumnMeta.class,new ColumnMeta(){
    @Override public Class<? extends Annotation> annotationType(){
      return ColumnMeta.class;
    }
    @Override public String friendlyName(){
      return dimension.getFriendlyName();
    }
    @Override public String description(){
      return dimension.getDescription();
    }
    @Override public String category(){
      return dimension.getCategory();
    }
    @Override public TableSource tableSource(){
      return buildTableSource(dimension.getTableSource());
    }
    @Override public String[] tags(){
      return dimension.getTags().toArray(new String[0]);
    }
    @Override public String[] values(){
      return dimension.getValues().toArray(new String[0]);
    }
    @Override public boolean isHidden(){
      return dimension.getHidden() != null && dimension.getHidden();
    }
    @Override public String filterTemplate(){
      return dimension.getFilterTemplate();
    }
    @Override public CardinalitySize size(){
      if (StringUtils.isEmpty(dimension.getCardinality())) {
        return CardinalitySize.UNKNOWN;
      }
      return CardinalitySize.valueOf(dimension.getCardinality().toUpperCase(Locale.ENGLISH));
    }
  }
);
  annotations.put(DimensionFormula.class,new DimensionFormula(){
    @Override public ArgumentDefinition[] arguments(){
      return getArgumentDefinitions(dimension.getArguments());
    }
    @Override public Class<? extends Annotation> annotationType(){
      return DimensionFormula.class;
    }
    @Override public String value(){
      return trimColumnReferences(dimension.getDefinition());
    }
  }
);
  String readPermission=dimension.getReadAccess();
  if (StringUtils.isNotEmpty(readPermission)) {
    annotations.put(ReadPermission.class,new ReadPermission(){
      @Override public Class<? extends Annotation> annotationType(){
        return ReadPermission.class;
      }
      @Override public String expression(){
        return readPermission;
      }
    }
);
  }
  if (dimension.getType().toUpperCase(Locale.ROOT).equals(ENUM_ORDINAL)) {
    annotations.put(Enumerated.class,getEnumeratedAnnotation(EnumType.ORDINAL));
  }
  if (dimension.getType().toUpperCase(Locale.ROOT).equals(TIME)) {
    annotations.put(Temporal.class,new Temporal(){
      @Override public Class<? extends Annotation> annotationType(){
        return Temporal.class;
      }
      @Override public TimeGrainDefinition[] grains(){
        int numGrains=dimension.getGrains() == null ? 0 : dimension.getGrains().size();
        TimeGrainDefinition[] definitions=new TimeGrainDefinition[numGrains];
        for (int idx=0; idx < numGrains; idx++) {
          Grain grain=dimension.getGrains().get(idx);
          definitions[idx]=new TimeGrainDefinition(){
            @Override public Class<? extends Annotation> annotationType(){
              return TimeGrainDefinition.class;
            }
            @Override public TimeGrain grain(){
              if (grain.getType() == null) {
                return TimeGrain.DAY;
              }
              return TimeGrain.valueOf(grain.getType().name());
            }
            @Override public String expression(){
              String sql=grain.getSql();
              if (StringUtils.isEmpty(sql)) {
                return "{{$$column.expr}}";
              }
              return grain.getSql();
            }
          }
;
        }
        return definitions;
      }
      @Override public String timeZone(){
        return "UTC";
      }
    }
);
  }
  return annotations;
}

