/** 
 * Returns the OpenAPI path for a relationship URL.
 * @return the OpenAPI 'Path' for a relationship URL(/books/{bookId}/relationships/author).
 * @throws IllegalStateException for errors.
 */
public PathItem getRelationshipPath(){
  if (lineage.isEmpty()) {
    throw new IllegalStateException("Root collections don't have relationships");
  }
  PathItem path=new PathItem();
  lineage.stream().forEach(item -> path.addParametersItem(item.getPathParameter()));
  String schemaName=getSchemaName(type);
  ApiResponse okSingularResponse=new ApiResponse().description("Successful response").content(new Content().addMediaType(JsonApi.MEDIA_TYPE,new MediaType().schema(new Datum(new Relationship(schemaName)))));
  ApiResponse okPluralResponse=new ApiResponse().description("Successful response").content(new Content().addMediaType(JsonApi.MEDIA_TYPE,new MediaType().schema(new Data(new Relationship(schemaName)))));
  ApiResponse okEmptyResponse=new ApiResponse().description("Successful response");
  Type<?> parentClass=lineage.peek().getType();
  RelationshipType relationshipType=dictionary.getRelationshipType(parentClass,name);
  if (relationshipType.isToMany()) {
    if (canRead(parentClass,name) && canRead(type)) {
      path.get(new Operation().tags(getTags()).description("Returns the relationship identifiers for " + name).responses(new ApiResponses().addApiResponse("200",okPluralResponse)));
    }
    if (canUpdate(parentClass,name)) {
      path.post(new Operation().tags(getTags()).description("Adds items to the relationship " + name).requestBody(new RequestBody().content(new Content().addMediaType(JsonApi.MEDIA_TYPE,new MediaType().schema(new Data(new Relationship(schemaName)))))).responses(new ApiResponses().addApiResponse("201",okPluralResponse)));
      path.patch(new Operation().tags(getTags()).description("Replaces the relationship " + name).requestBody(new RequestBody().content(new Content().addMediaType(JsonApi.MEDIA_TYPE,new MediaType().schema(new Data(new Relationship(schemaName)))))).responses(new ApiResponses().addApiResponse("204",okEmptyResponse)));
      path.delete(new Operation().tags(getTags()).description("Deletes items from the relationship " + name).requestBody(new RequestBody().content(new Content().addMediaType(JsonApi.MEDIA_TYPE,new MediaType().schema(new Data(new Relationship(schemaName)))))).responses(new ApiResponses().addApiResponse("204",okEmptyResponse)));
    }
  }
 else {
    if (canRead(parentClass,name) && canRead(type)) {
      path.get(new Operation().tags(getTags()).description("Returns the relationship identifiers for " + name).responses(new ApiResponses().addApiResponse("200",okSingularResponse)));
    }
    if (canUpdate(parentClass,name)) {
      path.patch(new Operation().tags(getTags()).description("Replaces the relationship " + name).requestBody(new RequestBody().content(new Content().addMediaType(JsonApi.MEDIA_TYPE,new MediaType().schema(new Datum(new Relationship(schemaName)))))).responses(new ApiResponses().addApiResponse("204",okEmptyResponse)));
    }
  }
  if (path.getGet() != null) {
    for (    Parameter param : getFilterParameters()) {
      path.getGet().addParametersItem(param);
    }
    for (    Parameter param : getPageParameters()) {
      path.getGet().addParametersItem(param);
    }
  }
  decorateGlobalResponses(path);
  decorateGlobalParameters(path);
  return path;
}

