private void publishAsync(final JetstreamEvent event){
  if (config.getEnableCassandra()) {
    worker.submit(new Runnable(){
      @Override public void run(){
        try {
          String metricName=(String)event.get(MCConstant.METRIC_NAME);
          String columnFamilyName=event.getEventType().toLowerCase();
          String groupId=null;
          Map<String,String> tags=null;
          if (event.get(MCConstant.METRIC_DEMENSION) != null) {
            MCMetricGroupDemension groupDemension=(MCMetricGroupDemension)event.get(MCConstant.METRIC_DEMENSION);
            groupId=groupDemension.getGroupId();
            if (event.get(MCConstant.TAG_TIME_IGNORE) != null) {
              tags=new HashMap<String,String>(groupDemension.getDimensions());
              tags.put(MCConstant.TAG_TIME_IGNORE,(String)event.get(MCConstant.TAG_TIME_IGNORE));
            }
 else {
              tags=groupDemension.getDimensions();
            }
          }
          if (groupId == null) {
            if (event.get(MCConstant.GROUP_ID) != null) {
              groupId=(String)event.get(MCConstant.GROUP_ID);
            }
 else {
              groupId=metricName;
            }
          }
          long count=0;
          if (event.get(MCConstant.AGGREGATED_COUNT) != null) {
            count=(Long)event.get(MCConstant.AGGREGATED_COUNT);
          }
 else {
            count=(Long)event.get(MCConstant.METRIC_COUNT);
          }
          long metricTime=(Long)event.get(MCConstant.METRIC_TIME);
          if (event.get(MCConstant.SHUTDOWN_FLUSH) == null && (event.get(MCConstant.FIRST_FLUSH) == null)) {
            metricTime=(metricTime / 60000) * 60000;
          }
          boolean counterTable=columnFamilyName.contains(COUNTER_TABLE);
          if (counterTable) {
            if (tags == null) {
              tags=new HashMap<String,String>();
              tags.put(MCCounterHelper.TAG_METRICTIME,String.valueOf(event.get(MCConstant.METRIC_TIME)));
            }
            Calendar c=Calendar.getInstance();
            c.setTimeInMillis(metricTime);
          }
          if (metricColumnFamily.get(metricName) == null || !metricColumnFamily.get(metricName).equals(columnFamilyName)) {
            if (counterTable) {
              if (updatestmtMap.get(columnFamilyName) == null) {
                if (tags != null && tags.size() > 0) {
                  prepareStatementsForUpdate(columnFamilyName,tags);
                }
 else {
                  prepareStatementsForUpdate(columnFamilyName,null);
                }
              }
            }
 else {
              if (stmtMap.get(columnFamilyName) == null) {
                if (tags != null && tags.size() > 0) {
                  prepareStatements(columnFamilyName,tags);
                }
 else {
                  prepareStatements(columnFamilyName,null);
                }
              }
            }
            metricColumnFamily.put(metricName,columnFamilyName);
          }
          if (config.getBatchSize() > 1) {
            MetricCounter metric=new MetricCounter(metricName,groupId,tags,count,metricTime);
            meticCounters.add(metric);
            int counterSize=meticCounters.size();
            if (counterSize >= config.getBatchSize()) {
              publishToCassandraInBatch();
            }
 else             if (event.get(LASTEVENTINBATCH) != null) {
              ePLBatchCount.increment();
              publishToCassandraInBatch();
            }
          }
 else {
            if (event.get(LASTEVENTINBATCH) != null) {
              ePLBatchCount.increment();
            }
            int parameterSize=4;
            if (tags != null) {
              parameterSize=4 + tags.size();
            }
            Object[] paramterValues=new Object[parameterSize];
            if (counterTable) {
              paramterValues[0]=count;
              paramterValues[1]=metricName;
              paramterValues[2]=groupId;
              paramterValues[3]=new Date(metricTime);
              int i=1;
              if (tags != null) {
                for (                Map.Entry<String,String> entry : tags.entrySet()) {
                  paramterValues[3 + i]=entry.getValue();
                  i++;
                }
              }
            }
 else {
              paramterValues[0]=metricName;
              paramterValues[1]=groupId;
              paramterValues[2]=new Date(metricTime);
              int i=1;
              if (tags != null) {
                for (                Map.Entry<String,String> entry : tags.entrySet()) {
                  paramterValues[2 + i]=entry.getValue();
                  i++;
                }
              }
              paramterValues[parameterSize - 1]=Long.valueOf(count).intValue();
            }
            publishToCassandra(paramterValues,event);
          }
        }
 catch (        Exception ex) {
          LOGGER.error("Error publising metrics in MetricCassandraCollector:" + ex.getMessage());
          cassandraErrorCount.increment();
          if (event.get(JetstreamReservedKeys.MessageAffinityKey.toString()) == null) {
            event.put(JetstreamReservedKeys.MessageAffinityKey.toString(),(String)event.get(MCConstant.METRIC_NAME));
          }
          getAdviceListener().retry(event,RetryEventCode.MSG_RETRY,ex.getMessage());
          eventSentToAdviceListener.increment();
          registerError(ex);
        }
      }
    }
);
  }
}

