@Override public void sendEvent(JetstreamEvent event) throws EventException {
  if (isPaused() || shutdownFlag.get()) {
    super.incrementEventDroppedCounter();
    return;
  }
  incrementEventRecievedCounter();
  if (MCCounterHelper.isMCCounterEvent(event)) {
    String metricName=(String)event.get(MCConstant.METRIC_NAME);
    Long total=null;
    if (MCCounterHelper.isAvgEvent(metricName)) {
      for (      Map.Entry<String,Object> entry : event.entrySet()) {
        if (entry.getKey().toLowerCase().contains("total")) {
          total=(Long)entry.getValue();
          break;
        }
      }
    }
    if (event.get(MCConstant.METRIC_COUNT) != null) {
      Long count=(Long)event.get(MCConstant.METRIC_COUNT);
      if (total != null) {
        ((AvgCounter)getCounterByMetricName(metricName,true)).inc(count,total);
      }
 else       getCounterByMetricName(metricName,false).inc(count);
    }
 else {
      getCounterByMetricName(metricName,false).inc();
    }
  }
 else   if (MCCounterHelper.isMCMultiCounterEvent(event)) {
    for (    Map.Entry<String,Object> entry : event.entrySet()) {
      if (entry.getKey().toLowerCase().contains("count")) {
        String metricName=entry.getKey();
        Long count=(Long)entry.getValue();
        if (count != null) {
          getCounterByMetricName(metricName,false).inc(count);
        }
 else {
          LOGGER.warn("Null Count returned by EPL, CountName:" + metricName);
        }
      }
    }
  }
 else   if (MCCounterHelper.isGroupByCounterEvent(event)) {
    if (getConfiguration().isEnableGroupByCounter()) {
      String metricName=(String)event.get(MCConstant.METRIC_NAME);
      String groupId=(String)event.get(MCConstant.GROUP_ID);
      if (groupId != null && groupId.trim().length() != 0) {
        long registerdFreq=getFrequencyByMetricName(metricName);
        if (event.get(MCConstant.FREQUENCY_IN_MIN) == null && registerdFreq <= 0) {
          setMetricFrequency(metricName,MetricFrequency.ONE_MINUTE.getValue());
        }
 else         if (event.get(MCConstant.FREQUENCY_IN_MIN) != null) {
          Integer frequencyInMin=(Integer)event.get(MCConstant.FREQUENCY_IN_MIN);
          long frequency=frequencyInMin * MetricFrequency.ONE_MINUTE.getValue();
          if (frequency > 0 && frequency != registerdFreq) {
            setMetricFrequency(metricName,frequency);
          }
        }
        metricTables.put(metricName,event.getEventType());
        Map<String,String> tags=new HashMap<String,String>(5);
        MCCounterHelper.isGroupByCounterEventWithTag(event,tags);
        Long total=null;
        if (MCCounterHelper.isAvgEvent(metricName)) {
          for (          Map.Entry<String,Object> entry : event.entrySet()) {
            if (entry.getKey().toLowerCase().contains("total")) {
              total=(Long)entry.getValue();
              break;
            }
          }
        }
        if (event.get(MCConstant.METRIC_COUNT) != null) {
          Long count=(Long)event.get(MCConstant.METRIC_COUNT);
          if (total != null) {
            getCounterByMetricDemensionAndInc(metricName,groupId,tags,true,count,total);
          }
 else {
            getCounterByMetricDemensionAndInc(metricName,groupId,tags,false,count,null);
          }
        }
 else {
          getCounterByMetricDemensionAndInc(metricName,groupId,tags,false,null,null);
        }
      }
 else {
        LOGGER.warn("Null or empty groupId returned by EPL, metricName:" + metricName);
      }
    }
  }
 else {
    evaluateRawEvent(event);
  }
}

public List<String> validate(){
  List<String> errors=new ArrayList<String>();
  if (config.getMaxIdleTime() <= 0) {
    errors.add("maxIdleTime must be positive");
  }
  if (config.getMaxIdleTime() > oldConfig.getMaxTimeSlots() * 1000) {
    errors.add("maxIdleTime is too big, should less than maxTimeSlots * 1000");
  }
  if (config.getEpl() == null || config.getRawEventDefinition() == null) {
    errors.add("must specify EPL and event definition to enable sessionization");
  }
  if (config.getMainSessionProfiles() == null) {
    errors.add("mainSessionProfile is null");
    return errors;
  }
 else {
    List<Integer> sessionTypes=new ArrayList<Integer>();
    List<String> sessionNames=new ArrayList<String>();
    for (    SessionProfile mp : config.getMainSessionProfiles()) {
      if (mp.getName() == null) {
        errors.add("session profile name is null");
        continue;
      }
      if (mp.getEpl() != null && mp.getRawEventDefinition() == null) {
        errors.add("must specify event definition to for sessinizer " + mp.getName());
        continue;
      }
      if (mp.getMaxActiveTime() < mp.getDefaultTtl()) {
        errors.add("maxActiveTime must be greater than defaultTtl");
      }
      if (sessionTypes.contains(mp.getSessionType())) {
        errors.add("duplicate session type id " + mp.getSessionType());
      }
 else {
        sessionTypes.add(mp.getSessionType());
      }
      if (sessionNames.contains(mp.getName())) {
        errors.add("duplicate session name " + mp.getName());
      }
 else {
        sessionNames.add(mp.getName());
      }
      List<SubSessionProfile> subSessionProfiles=mp.getSubSessionProfiles();
      if (subSessionProfiles != null) {
        List<String> subProfilerIds=new ArrayList<String>();
        for (        SubSessionProfile p : subSessionProfiles) {
          if (p.getName() == null) {
            errors.add("sub session profile name is null");
            continue;
          }
          if (p.getEpl() != null && mp.getRawEventDefinition() == null) {
            errors.add("Must specify EPL for sub sessinizer " + p.getName());
            continue;
          }
          if (p.getDefaultTtl() <= 0 || p.getDefaultTtl() > mp.getDefaultTtl()) {
            errors.add("sub sessionizer " + p.getName() + " default ttl must be positive and less than main sessionizer default ttl");
          }
          if (subProfilerIds.contains(p.getName())) {
            errors.add("duplicate sub session " + p.getName());
          }
 else {
            subProfilerIds.add(p.getName());
          }
        }
      }
    }
  }
  return errors;
}

@Override public void service(HttpServletRequest request,HttpServletResponse response) throws ServletException, IOException {
  ServletInputStream inputStream=request.getInputStream();
  if (inputStream != null) {
    inputStream.mark(Integer.MAX_VALUE);
  }
  try {
    String pathInfo=request.getPathInfo();
    if (pathInfo.startsWith(PATH_PING)) {
      ping(request,pathInfo,response);
    }
 else     if (pathInfo.startsWith(PATH_COUNTER)) {
      stats.incQueryRequestCount();
      getCounters(request,pathInfo,response);
    }
 else     if (pathInfo.startsWith(PATH_METRICGROUP)) {
      stats.incQueryRequestCount();
      getMetrics(request,pathInfo,response);
    }
 else {
      stats.incInvalidRequestCount();
      response.setStatus(HttpServletResponse.SC_NOT_FOUND);
    }
  }
 catch (  Throwable ex) {
    String requestTxt=readRequest(request);
    stats.setLastFailedRequest(readRequestHead(request) + requestTxt);
    stats.registerError(ex);
    response.setStatus(HttpServletResponse.SC_INTERNAL_SERVER_ERROR);
  }
 finally {
    response.addHeader("Access-Control-Allow-Origin","*");
    response.addHeader("Access-Control-Allow-Methods","*");
    response.addHeader("Access-Control-Allow-Headers","Content-Type");
  }
}

