/** 
 * Converts a  {@link QName} to a {@link Name}. A  {@link SOAPElement} is required to resolve namespaces.
 * @param qName the {@code QName} to convert
 * @param resolveElement a {@code SOAPElement} used to resolve namespaces to prefixes
 * @return the converted SAAJ Name
 * @throws SOAPException if conversion is unsuccessful
 * @throws IllegalArgumentException if {@code qName} is not fully qualified
 */
public static Name toName(QName qName,SOAPElement resolveElement) throws SOAPException {
  String qNamePrefix=qName.getPrefix();
  SOAPEnvelope envelope=getEnvelope(resolveElement);
  if (StringUtils.hasLength(qName.getNamespaceURI()) && StringUtils.hasLength(qNamePrefix)) {
    return envelope.createName(qName.getLocalPart(),qNamePrefix,qName.getNamespaceURI());
  }
 else   if (StringUtils.hasLength(qName.getNamespaceURI())) {
    Iterator<?> prefixes;
    if (getSaajVersion(resolveElement) == SAAJ_11) {
      prefixes=resolveElement.getNamespacePrefixes();
    }
 else {
      prefixes=resolveElement.getVisibleNamespacePrefixes();
    }
    while (prefixes.hasNext()) {
      String prefix=(String)prefixes.next();
      if (qName.getNamespaceURI().equals(resolveElement.getNamespaceURI(prefix))) {
        return envelope.createName(qName.getLocalPart(),prefix,qName.getNamespaceURI());
      }
    }
    return envelope.createName(qName.getLocalPart(),"",qName.getNamespaceURI());
  }
 else {
    return envelope.createName(qName.getLocalPart());
  }
}

/** 
 * Performs the given  {@linkplain SourceCallback callback} operation on a {@link Source}. Supports both the JAXP 1.4 {@link StAXSource} and the Spring 3.0 {@link StaxUtils#createStaxSource StaxSource}.
 * @param source source to look at
 * @param callback the callback to invoke for each kind of source
 */
public static void doWithSource(Source source,SourceCallback callback) throws Exception {
  if (source instanceof DOMSource) {
    callback.domSource(((DOMSource)source).getNode());
    return;
  }
 else   if (StaxUtils.isStaxSource(source)) {
    XMLStreamReader streamReader=StaxUtils.getXMLStreamReader(source);
    if (streamReader != null) {
      callback.staxSource(streamReader);
      return;
    }
 else {
      XMLEventReader eventReader=StaxUtils.getXMLEventReader(source);
      if (eventReader != null) {
        callback.staxSource(eventReader);
        return;
      }
    }
  }
 else   if (source instanceof SAXSource) {
    SAXSource saxSource=(SAXSource)source;
    callback.saxSource(saxSource.getXMLReader(),saxSource.getInputSource());
    return;
  }
 else   if (source instanceof StreamSource) {
    StreamSource streamSource=(StreamSource)source;
    if (streamSource.getInputStream() != null) {
      callback.streamSource(streamSource.getInputStream());
      return;
    }
 else     if (streamSource.getReader() != null) {
      callback.streamSource(streamSource.getReader());
      return;
    }
  }
  if (StringUtils.hasLength(source.getSystemId())) {
    String systemId=source.getSystemId();
    callback.source(systemId);
  }
 else {
    throw new IllegalArgumentException("Unknown Source type: " + source.getClass());
  }
}

private void createOperations(Definition definition,PortType portType) throws WSDLException {
  MultiValueMap<String,Message> operations=new LinkedMultiValueMap<>();
  for (  Object messageValue : definition.getMessages().values()) {
    Message message=(Message)messageValue;
    String operationName=getOperationName(message);
    if (StringUtils.hasText(operationName)) {
      operations.add(operationName,message);
    }
  }
  if (operations.isEmpty() && logger.isWarnEnabled()) {
    logger.warn("No operations were created, make sure the WSDL contains messages");
  }
  for (  String operationName : operations.keySet()) {
    Operation operation=definition.createOperation();
    operation.setName(operationName);
    List<Message> messages=operations.get(operationName);
    for (    Message message : messages) {
      if (isInputMessage(message)) {
        Input input=definition.createInput();
        input.setMessage(message);
        populateInput(definition,input);
        operation.setInput(input);
      }
 else       if (isOutputMessage(message)) {
        Output output=definition.createOutput();
        output.setMessage(message);
        populateOutput(definition,output);
        operation.setOutput(output);
      }
 else       if (isFaultMessage(message)) {
        Fault fault=definition.createFault();
        fault.setMessage(message);
        populateFault(definition,fault);
        operation.addFault(fault);
      }
    }
    operation.setStyle(getOperationType(operation));
    operation.setUndefined(false);
    if (logger.isDebugEnabled()) {
      logger.debug("Adding operation [" + operation.getName() + "] to port type ["+ portType.getQName()+ "]");
    }
    portType.addOperation(operation);
  }
}

/** 
 * Register all endpoints specified in the action map.
 * @param actionMap Map with action URIs as keys and endppint beans or bean names as values
 * @throws BeansException if an endpoint couldn't be registered
 * @throws IllegalStateException if there is a conflicting endpoint registered
 */
protected void registerEndpoints(Map<URI,Object> actionMap) throws BeansException {
  if (actionMap.isEmpty()) {
    logger.warn("Neither 'actionMap' nor 'mappings' set on SimpleActionEndpointMapping");
  }
 else {
    for (    Map.Entry<URI,Object> entry : actionMap.entrySet()) {
      URI action=entry.getKey();
      Object endpoint=entry.getValue();
      if (endpoint instanceof String) {
        endpoint=((String)endpoint).trim();
      }
      registerEndpoint(action,endpoint);
    }
  }
}

